import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import Image from 'react-bootstrap/Image';

function ModalMovie(props) {
    const [handleFav,setHandleFav]=useState({});
    const postReq = async () => {
        const serverUrl = `https://onlineserver-w9zu.onrender.com/trending`;
        const result = await axios.post(serverUrl);
        setHandleFav(result.data);

    }


    return (
        <>
            <Modal show={props.showFlag} onHide={props.handleClose}>
                <Modal.Header closeButton>
                    <Modal.Title>{props.movieData.title}</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Image src={`https://image.tmdb.org/t/p/original${props.movieData.poster_path}`} rounded width='100%'/>
                <div>
                <label htmlFor='op'> Write Your Opinion </label>
                <br></br>
                <input placeholder='write opinion' type="text" id="op" size='50'/>
                </div>
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={props.handleClose}>
                        Close
                    </Button>
                    <Button variant="primary" onClick={()=>{handleFav}}>
                        Add it
                    </Button>
                </Modal.Footer>
            </Modal>
        </>
    )
}

export default ModalMovie;
