import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import Image from 'react-bootstrap/Image';
import axios from "axios";
import {useEffect,useState} from "react";


function ModalMovie(props) {

    const [movList, setmoList] = useState([]);
    const sendReq = async () => {
        const serverUrl = `https://onlineserver-w9zu.onrender.com/trending`;
        const result = await axios.get(serverUrl);
        setmoList(result.data);

    }
    return (
        <>
            <Modal show={props.showFlag} onHide={props.handleClose}>
                <Modal.Header closeButton>
                    <Modal.Title>{props.movieData.title}</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Image src={props.movieData.poster_path} rounded width='100%'/>
                <div>
                <label htmlFor='op'> Write Your Opinion </label>
                <br></br>
                <input placeholder='write opinion' type="text" id="op" size='50'/>
                </div>
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={props.handleClose}>
                        Close
                    </Button>
                    <Button variant="primary" onClick={handleDataBase}>
                        Add it
                    </Button>
                </Modal.Footer>
            </Modal>
        </>
    )
}

export default ModalMovie;
