import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import Col from 'react-bootstrap/Col';
import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup';
import axios from 'axios';

function UpdateModal(props){
    const { showFlag, handleClose, movieData } = props;

    const handleSubmit = async (movieData) => {
        try {

            const serverUrl = `https://onlineserver-w9zu.onrender.com/getMovies`;
            const data = {
                id: movieData.target.id.value,
                title: movieData.target.id.value,
                release_date: movieData.target.id.value,
                poster_path: movieData.target.id.value,
                overview:movieData.target.id.value
               // Opinion:Opinion

            };
            console.log("sssssss");
            axios.post(serverUrl, data).then((res)=>{
console.log(res);
            });

            handleClose();

        } catch (error) {
            console.log(error);
        }
    };

    return(
        <>

        <Modal show={props.showFlag} onHide={props.handleclose}>
                <Modal.Header closeButton>
                    <Modal.Title>Update Form</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Form onSubmit={handleSubmit}>
                        <Form.Group>
                            <Form.Label>Movie ID</Form.Label>
                            <Form.Control
                                name="id"
                                type="text"
                                defaultValue={props.item.id}
                            />
                        </Form.Group>
                        <Form.Group>
                            <Form.Label>Movie Title</Form.Label>
                            <Form.Control
                                name="title"
                                type="text"
                                defaultValue={props.item.title}
                            />
                        </Form.Group>
                        <Form.Group>
                            <Form.Label>Movie Date</Form.Label>
                            <InputGroup hasValidation>
                                <Form.Control
                                    type="text"
                                    name="release_date"
                                    defaultValue={props.item.release_date}
                                />
                            </InputGroup>
                        </Form.Group>
                        <Form.Group>
                            <Form.Label>Movie Image</Form.Label>
                            <InputGroup hasValidation>
                                <Form.Control
                                    type="text"
                                    name="poster_path"
                                    defaultValue={props.item.poster_path}
                                />
                            </InputGroup>
                        </Form.Group>
                        <Form.Group>
                            <Form.Label>Movie overview</Form.Label>
                            <InputGroup hasValidation>
                                <Form.Control
                                    type="text"
                                    name="overview"
                                    defaultValue={props.item.overview}
                                />
                            </InputGroup>
                        </Form.Group>
                        <Button type="submit">Submit form</Button>
                    </Form>
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={props.handleclose}>
                        Close
                    </Button>
                </Modal.Footer>
            </Modal>


        </>
    )

}
export default UpdateModal
