import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import Image from 'react-bootstrap/Image';
import React, { useState } from 'react';
import axios from 'axios';


function ModalMovie(props) {
    const { show, handleClose, movie, onMovieAdded } = props;
    const [comment, setComment] = useState('');
    const handleSubmit = async () => {
      try {
        const serverUrl = `${process.env.REACT_APP_SERVER_URL || "http://localhost:3004"}/getMovies`;
        const data = {
          id: movie.id,
          title: movie.title,
          poster_path: movie.poster_path,
          comment: comment
        };
          await axios.post(serverUrl, data);
        handleClose();
        onMovieAdded();
      } catch (error) {
        console.log(error);
      }
    };


    return (
        <>
            <Modal show={props.showFlag} onHide={props.handleClose}>
                <Modal.Header closeButton>
                    <Modal.Title>{props.movieData.title}</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Image src={`https://image.tmdb.org/t/p/original${props.movieData.poster_path}`} rounded width='100%'/>
                <div>
                <label htmlFor='op'> Write Your Opinion </label>
                <br></br>
                <input placeholder='write opinion' type="text" valu="op" size='50'/>
                </div>
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={props.handleClose}>
                        Close
                    </Button>
                    <Button variant="primary" onClick={()=>{handleSubmit}}>
                        Add it
                    </Button>
                </Modal.Footer>
            </Modal>
        </>
    )
}

export default ModalMovie;
